import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; 
import Loading from './Loading';
import Error from './Error';
import { FaRegHeart, FaHeart } from "react-icons/fa";
import { FiShare } from 'react-icons/fi'
import { BiCommentDetail } from 'react-icons/bi'
import { RiSendPlaneFill } from 'react-icons/ri'
import { IconContext } from 'react-icons';
import { Helmet } from 'react-helmet'

const Blogs = () => {
    const { id } = useParams();
    const [liked, setLiked] = useState(null);
    const [blog, setBlog] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState(null)
    const [isOpen, setIsOpen] = useState(false)
    const [matches, setMatches] = useState(0)

    useEffect(() => {
        const abortCont = new AbortController();

        fetch('http://192.168.1.98:5000/blogs/' + id, { signal: abortCont.signal })
            .then(res => {
                if (!res.ok) {
                    throw new Error('Could not get data from the database');
                }
                return res.json()
            })
            .then(data => {
                setBlog(data)
                if (data.liked === true) {
                    setLiked(true)
                } else if (data.liked === false) {
                    setLiked(false);
                }
                setIsLoading(false)
                setError(null)
            })
            .catch(err => {
                if (err.name === 'AbortError') {
                    // empty if statement
                } else {
                    setIsLoading(false)
                    setError(err)
                }

            })

        return () => abortCont.abort()

    }, [id, liked, matches])

    const likePost = () => {
        liked ? setLiked(false) : setLiked(true);
        fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ liked: !blog.liked }),
        })
            .then(() => {

            })
    }

    const toggleReply = (e, id) => {
        let x = e.target.value
        let y = id.id

        // x == y ? setMatches(y) : setMatches(null)
        matches === y && isOpen ? setIsOpen(false) : setIsOpen(true);
    }


    return (
        <>
            { isLoading && <Loading /> }
            { error && <Error/> }
            { blog && (
                <div className="blog-details">
                    <Helmet>
                        <meta charset='utf-8' />
                        <title>{blog.title}</title>
                    </Helmet>

                    <div className="main-blog-place">
                        <article>
                            <h1>{blog.title}</h1>
                            <p> {blog.author} </p>
                            <img className="blog-img" src={blog.image} alt="" />
                            <div className="icons-source">
                                <p className='source'>Image source <a href={blog.image}>Unsplash</a></p>
                                <div className="iconsSource">
                                    {liked ? <IconContext.Provider value={{ color: 'red', className: 'love-icon' }}>
                                        <FaHeart onClick={likePost} />
                                    </IconContext.Provider>

                                        : <FaRegHeart className='test' onClick={likePost} />}

                                    <FiShare />
                                    <BiCommentDetail />
                                </div>
                            </div>
                            <div className='blog-body'>{blog.body}</div>
                        </article>


                        <div className="comment">
                            <input type="text" placeholder="Add a comment" />
                            <IconContext.Provider value={{ color: 'black', className: 'comment-icon', size: '1.5rem' }}>
                                <RiSendPlaneFill />
                            </IconContext.Provider>
                        </div>
                    </div>

                    <div className="comment-box">
                        <div className="comment-box-main">
                            <h1>Comments</h1>
                            <div className="comment-for-box">
                                <input type="text" placeholder="Add a comment" />
                                <IconContext.Provider value={{ color: 'white', className: 'comment-for-box-icon', size: '1.5rem' }}>
                                    <RiSendPlaneFill className='sendPaper'/>
                                </IconContext.Provider>
                            </div>
                            <div className="discussion">
                                <div className="main-comment">
                                    {
                                        blog.discussion.map((discussion) => (
                                            <div key={discussion.id}>
                                                <div className="commented">
                                                    {/* {console.log(discussion)} */}
                                                    <h3>{discussion.user}</h3>
                                                    <h4>{discussion.comment}</h4>
                                                    <button value={discussion.id} onClick={(e) => toggleReply(e, discussion)}>toggle</button>
                                                    {

                                                        matches === discussion.id && 
                                                        (
                                                            isOpen ?
                                                                <div className="replies nav-fadeIn">
                                                                    {
                                                                        discussion.replies.map((replied) => (
                                                                            <div key={replied.id} className="replied">
                                                                                <h4>{replied.replier}</h4>
                                                                                <h4>{replied.reply}</h4>
                                                                            </div>

                                                                        ))
                                                                    }
                                                                </div>
                                                                : <div className="replies nav-fadeOut">
                                                                </div>
                                                        )


                                                    }
                                                </div>


                                            </div>

                                        ))
                                    }

                                </div>
                            </div>

                        </div>
                    </div>
                </div>


            )}
        </>
    );
}

export default Blogs;
