import { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import useFetch from './useFetch';
import Loading from './Loading';
import Error from './Error';
import { FaRegHeart, FaHeart } from "react-icons/fa";
import { FiShare } from 'react-icons/fi'
import { BiCommentDetail } from 'react-icons/bi'
import { IconContext } from 'react-icons';

const Blogs = () => {
    const history = useHistory();
    const { id } = useParams();
    const [liked, setLiked] = useState(false);
    const [blog, setBlog] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() => {
        const abortCont = new AbortController();

        setTimeout(() => {
            fetch('http://192.168.1.98:5000/blogs/' + id, { signal: abortCont.signal })
                .then(res => {
                    if (!res.ok) {
                        throw new Error('Could not get data from the database');
                    }
                    return res.json()
                })
                .then(data => {
                    setBlog(data)
                    setIsLoading(false)
                    setError(null)
                })
                .catch(err => {
                    if (err.name === 'AbortError') {
                        // empty if statement
                    } else {
                        setIsLoading(false)
                        setError(err)
                    }

                })
        }, 1500);

        return () => abortCont.abort()

    }, ['http://192.168.1.98:5000/blogs/' + id])

    const deleteBlog = async () => {
        await fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'DELETE'
        })
        .then(() => {
            history.push('/');
        })
    }

    const likePost = () => {
        liked ? setLiked(false) : setLiked(true);
        fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ liked: !blog.liked }),
        })
        .then(() => {

        })
    }

    return (
        <div className="blog-details">
            { isLoading && <Loading /> }
            { error && <Error/> }
            { blog && (
                <article>
                    <h1>{blog.title}</h1>
                    <p> {blog.author} </p>
                    <img className="blog-img" src={blog.image} alt="" />
                    <div className="icons-source">
                        <p className='source'>Image source <a href={blog.image}>Unsplash</a></p>
                        <div className="iconsSource">
                            {liked ? <IconContext.Provider value={{ color: 'red' }}>
                                        <FaHeart onClick={likePost} />
                                    </IconContext.Provider> 

                                : <FaRegHeart className='test' onClick={likePost} /> }

                            <FiShare />
                            <BiCommentDetail />
                        </div>
                    </div>
                    <div className='blog-body'>{ blog.body }</div>
                    <button onClick={deleteBlog}>Delete</button>
                </article>
            )}
        </div>
    );
}

export default Blogs;
