import { RiSendPlaneFill } from 'react-icons/ri';
import { IconContext } from 'react-icons';
import { useState } from 'react'

const AddComment = ({ handleComment }) => {
    const [comment, setComment] = useState('');


	return (

		<form className="addComment" onSubmit={(e) => handleComment(e, comment)}>
			<input
				type="text"
				placeholder="Add a comment"
				value={comment}
				onChange={(e) => setComment(e.target.value)}
			/>
			<IconContext.Provider value={{ color: 'rgb(14, 13, 13)', className: 'comment-icon', size: '1.5rem' }}>
				<RiSendPlaneFill type="submit" onClick={() => handleComment(comment)} />
			</IconContext.Provider>
		</form>
	);
};

export default AddComment;
