import React, { useState, useEffect } from 'react'
import BlogList from './BlogList';
import Loading from './Loading';
import Error from './Error';

const Home = ({ title }) => {
    const [blogs, setBlogs] = useState(null)
    const [blogIsLoading,  setBlogIsLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() => {
        setTimeout(() => {
            fetch('http://localhost:5000/blogs')
                .then(res => {
                    if(!res.ok){
                        throw new Error('Could not get blogs from the database');
                    }

                    return res.json()
                })
                .then(data => {
                    setBlogs(data)
                    setBlogIsLoading(false)
                    setError(null)
                })
                .catch(err => {
                    setBlogIsLoading(false)
                    setError(err)
                    alert(err.message)
                })
        }, 5000);

    }, []) 

    //add a blog

    //delete a blog
    const deleteBlog = async (id) => {
        const res = await fetch(`http://localhost:5000/blogs/${id}`, {
            method: 'DELETE',
        })

        //gotta check if the blog was deleted from the server
        res.status === 200
        ? setBlogs(blogs.filter(blog => blog.id !== id))
        : alert('There was a problem deleting the blog')
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            { error && <Error/> }
            { blogIsLoading && <Loading /> }
            { blogs && <BlogList blogs={blogs} deleteBlog={deleteBlog} />}
        </div>
    );
}

export default Home;
