import { useState, useEffect } from 'react'

const useFetch = (url) => {
    const [data, setData] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() => {
        setTimeout(() => {
            fetch(url)
                .then(res => {
                    if (!res.ok) {
                        throw new Error('Could not get blogs from the database');
                    }

                    return res.json()
                })
                .then(data => {
                    setData(data)
                    setIsLoading(false)
                    setError(null)
                })
                .catch(err => {
                    setIsLoading(false)
                    setError(err)
                })
        }, 3000);

    }, [])

    return { data, isLoading, error }
}

export default useFetch;
