import BlogList from './BlogList';
import Loading from './Loading';
import Error from './Error';
import useFetch from './useFetch';

const Home = ({ title }) => {
    const { data: blogs, error, isLoading } = useFetch('http://localhost:5000/blogs');

    return (
        <div className="home">
            <h1>{title}</h1> 
            { error && <Error/> }
            { isLoading && <Loading /> }
            { blogs && <BlogList blogs={blogs}  />}
            {/* deleteBlog={deleteBlog} */}
        </div>
    );
}

export default Home;
