import { IconContext } from 'react-icons';
import { FiShare } from 'react-icons/fi'
import { BsPencilSquare } from 'react-icons/bs'
import { MdMoreVert, MdDelete } from 'react-icons/md';
import { useState } from 'react';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const BlogMenu = ({ blog }) => {
    const [anchorEl, setAnchorEl] = useState(null)

    const handleClick = (e) => {
        setAnchorEl(e.currentTarget)
    }

    const handleClose = () => {
        setAnchorEl(null)
    }

    const toShare = {
        text: `${blog.title}`,
        url: `http://192.168.1.98:5000/blogs/${blog.id}`,
    }

    const shareBlog = () => {
        try {
            navigator.share(toShare)
        } catch(err) {
            alert("Ran into an error when trying to share. Error: " + err.message)
            console.log("Ran into an error when trying to share. Error: " + err.message)
        }
    }

    const deleteBlog = async (id) => {
        const res = fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'DELETE'
        })
        res.status === 200
        ? blog.filter(blog => blog.id !== id)
        : alert("Ran into an error when trying to delete")
    }

    return (
        <div>
            <IconContext.Provider value={{ size: '1.75rem' }}>
                <div>
                    <MdMoreVert className="menuIcon" aria-controls="simple" aria-haspopup="true" onClick={handleClick} />
                </div>
            </IconContext.Provider>

            <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
            >
                <MenuItem>

                    <div className="menu-item">

                        <IconContext.Provider value={{ weight: '1', size: '1.5rem' }}>
                            <div className='blog-menu-icon'>
                                <BsPencilSquare onClick={() => console.log("Edit Blog")} />
                            </div>
                        </IconContext.Provider>

                        <IconContext.Provider value={{ size: '1.75rem' }}>
                            <div className='blog-menu-icon'>
                                <MdDelete onClick={deleteBlog}/>
                            </div>
                        </IconContext.Provider>

                        <IconContext.Provider value={{ size: '1.5rem' }}>
                            <div className='blog-menu-icon-last'>
                                <FiShare onClick={shareBlog} />
                            </div>
                        </IconContext.Provider>

                    </div>

                </MenuItem>
            </Menu>

        </div>

    );
}

export default BlogMenu;
