import React, { useState, useEffect } from 'react'
import BlogList from './BlogList';

const Home = ({ title }) => {
    const [blogs, setBlogs] = useState([])
    // const [blogIsLoading,  setBlogIsLoading] = useState(true)

    useEffect(() => {
        // fetch('http://localhost:5000/blogs')
        //     .then(res => {
        //         return res.json()
        //     })
        //     .then(data => {
        //         setBlogs(data)
        //         // setBlogIsLoading(false)
        //     })

        const getBlogs = async () => {
            const blogsFromServer = await fetchBlogs();
            setBlogs(blogsFromServer)
        }

        getBlogs();
    }, []) 

    //add a blog

    //delete a blog
    const deleteBlog = async (id) => {
        const res = await fetch(`http://localhost:5000/blogs/${id}`, {
            method: 'DELETE',
        })

        //gotta check if the blog was deleted from the server
        res.status === 200
        ? setBlogs(blogs.filter(blog => blog.id !== id))
        : alert('There was a problem deleting the blog')
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            {/* { blogIsLoading && <div>Loading...</div> } */}
            { blogs.length > 0 ? (<BlogList blogs={blogs} deleteBlog={deleteBlog} />) : ('No blogs yet.')}
        </div>
    );
}

export default Home;
