import React, { useState, useEffect } from 'react'
import BlogList from './BlogList';

const Home = ({ title }) => {
    const [blogs, setBlogs] = useState([])

    useEffect(() => {
        const getBlogs = async () => {
            const blogsFromServer = await fetchBlogs();
            setBlogs(blogsFromServer)
        }

        getBlogs();
    }, [])

    //fetch all blogs
    const fetchBlogs = async () => {
        const res = await fetch('http://localhost:5000/blogs')
        const data = res.json();

        return data;
    }

    //add a blog

    //delete a blog
    const deleteBlog = (id) => {
        const newBlog = blogs.filter(
            blog => blog.id !== id
        )
        setBlogs(newBlog)
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            { blogs.length > 0 ? 
                (
                    <BlogList 
                        blogs={blogs} 
                        deleteBlog={deleteBlog}
                    />
                ) : (
                    'No blogs yet.'
                )}
        </div>
    );
}

export default Home;
