import { RiSendPlaneFill } from 'react-icons/ri';
import { IconContext } from 'react-icons';
import { useState } from 'react'

const AddComment = ({ handleComment }) => {
    const [comment, setComment] = useState('');
    const handleChange = (e) => {
        let newComment = e.target.value;
        setComment(newComment);
    }

	return (
		<>
			<input type="text" placeholder="Add a comment" value={comment} onChange={(e) => handleChange(e)} />
			<IconContext.Provider value={{ color: 'rgb(14, 13, 13)', className: 'comment-icon', size: '1.5rem' }}>
				<RiSendPlaneFill onClick={handleComment} />
			</IconContext.Provider>
		</>
	);
};

export default AddComment;
