import { RiSendPlaneFill } from 'react-icons/ri';
import { IconContext } from 'react-icons';
import { useState } from 'react'

const AddComment = ({ handleComment }) => {
    const [comment, setComment] = useState('');

	return (
		<div className="main-addComment">
			<form className="addComment" onSubmit={() => handleComment(comment)}>
				<input
					type="text"
					placeholder="Add a comment"
					value={comment}
					onChange={(e) => setComment(e.target.value)}
				/>
				<IconContext.Provider value={{className: 'comment-icon', color: 'primary'}}>
					<RiSendPlaneFill type="submit" />
				</IconContext.Provider>
			</form>
		</div>
	);
};

export default AddComment;
