import { FaRegHeart, FaHeart } from "react-icons/fa";
import { IconContext } from 'react-icons';


const LikedComment = ({ key, liked, likeComment }) => {
    return (
        <>
            { discussion.liked ? <IconContext.Provider  value={{ color: 'red', className: 'love-icon' }}>
                <FaHeart key={key} onClick={likeComment} />
            </IconContext.Provider>

                : <FaRegHeart key={key} onClick={likeComment} /> }
        </>
    );
}

export default LikedComment;
