import React, { useState } from 'react'

const Home = ({ title }) => {
    const [name, setName] = useState('Kayode')
    const [age, setAge] = useState(18)
    const handleClick = () => {
        setName("Ogunmakinwa")
        setAge(20)
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            <p>{name} is { age } years old.</p>
            <button onClick={handleClick}>Click Me</button>
        </div>
    );
}

export default Home;
