import { useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import useFetch from './useFetch';
import Loading from './Loading';
import Error from './Error';
import { FaRegHeart, FaHeart } from "react-icons/fa";
import { FiShare } from 'react-icons/fi'
import { BiCommentDetail } from 'react-icons/bi'
import { IconContext } from 'react-icons';

const Blogs = () => {
    const history = useHistory();
    const { id } = useParams();
    const { data: blog, error, isLoading } = useFetch('http://192.168.1.98:5000/blogs/' + id);
    // let isLiked = blog.liked;



    const deleteBlog = async () => {
        fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'DELETE'
        })
        .then(() => {
            history.push('/');
        })
    }

    let [liked, setLiked] = useState();

    // if (isLiked === true) {
    //     setLiked(true);
    // }

    const likePost = () => {
        liked ? setLiked(false) : setLiked(true);

        fetch(`http://192.168.1.98:5000/blogs/${blog.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ liked : liked }),
        })
    }

    return (
        <div className="blog-details">
            { isLoading && <Loading /> }
            { error && <Error/> }
            { blog && (
                <article>
                    <h1>{blog.title}</h1>
                    <p> {blog.author} </p>
                    <img className="blog-img" src={blog.image} alt="" />
                    <div className="icons-source">
                        <p className='source'>Image source <a href={blog.image}>Unsplash</a></p>
                        <div className="iconsSource">
                            { liked ? <FaHeart onClick={likePost} />
                                    : <IconContext.Provider value={{ color: 'red' }}>
                                <FaRegHeart onClick={likePost} />
                            </IconContext.Provider> }

                            <FiShare />
                            <BiCommentDetail />
                        </div>
                    </div>
                    <div className='blog-body'>{ blog.body }</div>
                    <button onClick={deleteBlog}>Delete</button>
                </article>
            )}
        </div>
    );
}

export default Blogs;
