import { FaRegHeart, FaHeart } from "react-icons/fa";
import { IconContext } from 'react-icons';
import { useState, useEffect } from 'react';

const Replies = ({ comments, id }) => {
    const [liked, setLiked] = useState(false);
    const [error, setError] = useState(false)
    const [replies, setReplies] = useState(null)
    const [data, setData] = useState(null)

    useEffect(() => {
        const abortCont = new AbortController();

        fetch('http://192.168.1.98:5000/blogs/' + id, { signal: abortCont.signal })
            .then(res => {
                // if (!res.ok) {
                //     throw new Error('Could not get data from the database');
                // }
                return res.json()
            })
            .then(data => {
                console.log(data)
                setData(data)

            })
            .catch(err => {
                if (err.name === 'AbortError') {
                    // empty if statement
                } else {
                    setError(true)
                }

            })

        return () => abortCont.abort()

    }, [id, liked])

    const likeComment = (id) => {
        fetch('http://192.168.1.98:5000/blogs/' + id, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({  })
        })

        liked ? setLiked(false) : setLiked(true)

    }

    return (
        <>
            {
                data.discussion.map((discussion) => (
                    <div key={discussion.id}>
                        <div className="comment">
                            <img className='commentImg' src={discussion.thumbnail} alt="" />
                            <div className="commentBody">
                                <p> <span>{discussion.user}</span> {discussion.comment}</p>

                            </div>
                            <div className="commentEmoji">
                                {liked ? <IconContext.Provider value={{ color: 'red', className: 'love-icon' }}>
                                    <FaHeart onClick={() => likeComment(discussion.id)} />
                                </IconContext.Provider>

                                    : <FaRegHeart onClick={() => likeComment(discussion.id)} />}
                            </div>
                        </div>
                    </div>
                ))

            }
        </>

    );
}

export default Replies;
