import { useParams } from 'react-router-dom';
import useFetch from './useFetch';

const Blogs = ({ blog }) => {
    const { id } = useParams();
    const { data: blogs, error, isLoading } = useFetch('http://192.168.1.98:5000/blogs');

    return (
        <>
            <h1>Blog - {id}</h1>
            <h1>{blog.title}</h1>
            <h4>{blog.author}</h4>
            <h5>{blog.body}</h5>
        </>
    );
}

export default Blogs;
