import { IconContext } from 'react-icons';
import { MdMoreVert, MdDelete } from 'react-icons/md';
import { IoShareOutline, IoPencilOutline } from "react-icons/io5";
import { useState } from 'react';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const BlogMenu = ({ blog, deleteBlog }) => {
    const [anchorEl, setAnchorEl] = useState(null)

    const handleClick = (e) => {
        setAnchorEl(e.currentTarget)

        // onClick = {() => deleteBlog(blog.id)}
    }

    const handleClose = () => {
        setAnchorEl(null)
    }

    return (
        <div>
            <IconContext.Provider value={{ color: 'rgb(95, 94, 94)', size: '1.75rem' }}>
                <div>
                    <MdMoreVert aria-controls="simple" aria-haspopup="true" onClick={handleClick} />
                </div>
            </IconContext.Provider>



            <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
            >
                <MenuItem onClick={() => deleteBlog(blog.id)} >
                    {/* Delete */}

                    <div className="menu-item">

                        <IconContext.Provider value={{ color: '#333', size: '1.5rem' }}>
                            <div className='blog-menu-icon'>
                                <IoPencilOutline />
                            </div>
                        </IconContext.Provider>

                        <IconContext.Provider value={{ color: '#f44336', size: '1.75rem' }}>
                            <div className='blog-menu-icon'>
                                <MdDelete/>
                            </div>
                        </IconContext.Provider>

                        <IconContext.Provider value={{ color: '#f44336', size: '1.5rem' }}>
                            <div className='blog-menu-icon-last'>
                                <IoShareOutline />
                            </div>
                        </IconContext.Provider>

                    </div>

                </MenuItem>
            </Menu>

        </div>

    );
}

export default BlogMenu;
