import Loading from './Loading';
import Error from './Error';
import useFetch from './useFetch';

const Home = () => {
    const { data: blogs, error, isLoading } = useFetch('http://192.168.1.98:5000/blogs');


    return (
		<div className="home">
			{error && <Error />}
			{isLoading && <Loading />}
			{blogs && <div className="homepage">
                { 
                    <h1>{blogs.title}</h1>
                 }
            </div>}
		</div>
	);
}

export default Home;
