import useMediaQuery from '@material-ui/core/useMediaQuery';
import NavBarMenu from './NavBarMenu'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const matches = useMediaQuery("(max-width: 480px)");
    const [isOpen, setIsOpen] = useState(false)

    const openMenu = () => {
        setIsOpen(true)
    }
    const closeMenu = () => {
        setIsOpen(false)
    }

    return (
        <div className="main-nav">
            <nav className="navbar">
                <Link to="/"><h1>Mini Blog</h1></Link>

                {matches === true
                    ? <NavBarMenu isOpen={isOpen} openMenu={openMenu} closeMenu={closeMenu} />

                    : <div className="links">
                        <Link to="/">Home</Link>
                        <Link to="/create">New Blog</Link>
                    </div>
                }
            </nav>

            { 
                isOpen ? (
                    <div className='nav-mobile'>
                        <div className="nav-fadeIn">
                            <Link to="/" onClick={closeMenu} >Home</Link>
                            <Link className="last-a" to="/create" onClick={closeMenu} >New Blog</Link>
                        </div>
                    </div>
                )
                : (
                    <div className={'nav-fadeOut'}>

                    </div>
                )

             }
        </div>
    );
}

export default Navbar;
