import { IoMenu, IoClose } from "react-icons/io5";
import { IconContext } from 'react-icons';

const NavBarMenu = ({ isOpen, setIsOpen, openMenu, closeMenu }) => {

    return (
        <IconContext.Provider value={{ color: '#333', size: '2.5rem' }}>
            <div className='nav-icon'>
                {
                    isOpen ? <IoClose onClick={closeMenu} /> : <IoMenu onClick={openMenu} />
                }
            </div>
        </IconContext.Provider>
    );
}

export default NavBarMenu;
