import LikedComment from './LikedComment';
import LikedReply from './LikedReply';
import { useState, useEffect } from 'react';
import AddComment from './AddComment';
import CommentGoBack from './CommentGoBack';
import { useParams } from 'react-router-dom';

const Discussions = () => {
	const { id } = useParams();
	const [error, setError] = useState(false);
	const [discussions, setDiscussions] = useState(null);
	const [replies, setReplies] = useState(null);

	useEffect(() => {
		const abortCont = new AbortController();

		fetch(`http://192.168.1.98:5000/blogs/${id}`, { signal: abortCont.signal })
			.then((res) => {
				if (!res.ok) {
					throw new Error('Could not get data from the database');
				}
				return res.json();
			})
			.then((data) => {
				setDiscussions(data);
			})
			.catch((err) => {
				if (err.name === 'AbortError') {
					// empty if statement
				} else {
					setError(true);
				}
			});

		return () => abortCont.abort();
	}, [id, discussions]);

	//Like a comment
	const likeComment = (did) => {
        const url = `http://192.168.1.98:5000/blogs/${id}`;
		setDiscussions({
			...discussions,
			discussion: discussions.discussion.map((discussion) => {
				if (discussion.id === did) {
					return { ...discussion, liked: !discussion.liked };
				} else {
					return discussion;
				}
			}),
		});
        // fetch(url, {
		// 	method: 'PATCH',
		// 	headers: {
		// 		'Content-Type': 'application/json',
		// 	},
		// 	body: JSON.stringify({
		// 		...discussions,
		// 		discussion: discussions.discussion.map((discussion) => {
		// 			if (discussion.id === did) {
		// 				return { ...discussion, liked: !discussion.liked };
		// 			} else {
		// 				return discussion;
		// 			}
		// 		}),
		// 	}),
		// });
	};

	//Like a reply
	const likeReply = (e, xid, did) => {
		const url = `http://192.168.1.98:5000/blogs/${id}`;
		// setDiscussions({
		// 	...discussions,
		// 	discussion: discussions.discussion.map((discussion) => {
		// 		if (discussion.id === xid) {
		// 			discussion.replies.map((reply) => {
		// 				if (reply.id === did) {
		// 					return { ...reply, liked: !reply.liked };
		// 				} else {
		// 					return reply;
		// 				}
		// 			})
		// 		} else {
		// 			return discussion;
		// 		}
		// 		return discussion;
		// 	}),
		// });

		setDiscussions({
			...discussions,
			discussion: discussions.discussion.map((discussion) => {
				if (discussion.id === xid) {
					return { ...discussion,  replies: {
						replies: discussion.replies.map((reply) => {
							console.log(reply)
							if(reply.id === did) {

							} else {
								return reply;
							}
						})
					} };
				} else {
					return discussion;
				}
			}),
		});
		// fetch(url, {
		// 	method: 'PATCH',
		// 	headers: {
		// 		'Content-Type': 'application/json',
		// 	},
		// 	body: JSON.stringify({
		// 		...discussions,
		// 		discussion: discussions.discussion.replies.map((reply) => {
		// 			if (reply.id === did) {
		// 				return { ...reply, liked: !reply.liked };
		// 			} else {
		// 				return reply;
		// 			}
		// 		}),
		// 	}),
		// });
	};

	return (
		<>
			{error && <div className="commentError">Cannot retrieve comments </div>}
			{discussions && (
				<>
					<CommentGoBack />
					<AddComment />

					<div className="commentSection">
						{discussions.discussion.map((discussion, index) => (
							<div key={discussion.id}>
								<div onDoubleClick={() => likeComment(discussion.id)} className="comment">
									<img className="commentImg" src={discussion.thumbnail} alt="userImg" />
									<div className="commentBody">
										<p>
											<span>{discussion.user}</span> {discussion.comment}
										</p>

										<div className="commentEmoji">
											<LikedComment
												liked={discussion.liked}
												key={index}
												onClick={() => likeComment(discussion.id)}
											/>
										</div>
									</div>
								</div>
								<div className="replies">
									{discussion.replies.map((reply, index) => (
										<div key={reply.id}>
											<div className="reply">
												<img className="replyImg" src={reply.thumbnail} alt="userImg" />
												<div className="replyBody">
													<p>
														<span>{reply.replier}</span> {reply.reply}
													</p>

													<div className="replyEmoji">
														<LikedReply
															liked={reply.liked}
															key={index}
															onClick={(e) => likeReply(e, discussion.id, reply.id)}
														/>
													</div>
												</div>
											</div>
										</div>
									))}
								</div>
							</div>
						))}
					</div>
				</>
			)}
		</>
	);
};

export default Discussions;
