import useFetch from './useFetch';
import { FaRegHeart, FaHeart } from "react-icons/fa";
import { IconContext } from 'react-icons';
import { useState } from 'react';

const Replies = ({ comments, id }) => {

    const { data: blogs, error } = useFetch(`http://192.168.1.98:5000/blogs/${id}`);
    const [liked, setLiked] = useState(null);
    const [commentMatch, setCommentMatch] = useState(false);

    const likeComment = (e, val) => {
        let x = Number(e.target.parentNode.accessKey)
        console.log(x + 'from val')
        let y = val.id
        console.log(y + 'they match')
        x == y ? setCommentMatch(true) : setCommentMatch(null)
        commentMatch && liked ? setLiked(false) : setLiked(true) 

    }

    return (
        <>
            {error && <div className="commentError">Cannot retrieve comments </div>}
            { blogs && <div className="commentSection">
                {
                    blogs.discussion.map((discussion) => (
                        <div key={discussion.id}>
                            <div className="comment">
                                <img className='commentImg' src={discussion.thumbnail} alt=""/>
                                <div className="commentBody">
                                    <p> <span>{discussion.user}</span> {discussion.comment}</p>

                                </div>
                                <div className="commentEmoji" accessKey={discussion.id}>
                                    {
                                        commentMatch === discussion.id && liked  
                                            ? <IconContext.Provider value={{color: 'red', className: 'love-icon' }}>
                                                <FaHeart onClick={(e) => likeComment(e, discussion)} />
                                            </IconContext.Provider>

                                        : <FaRegHeart onClick={(e) => likeComment(e, discussion)} />

                                    }
                                </div>
                            </div>
                        </div>
                    ))
                }
            </div> }
        </>

    );
}

export default Replies;
