import React, { useState, useEffect } from 'react'
import BlogList from './BlogList';

const Home = ({ title }) => {
    const [blogs, setBlogs] = useState([])

    useEffect(() => {
        fetch('http://localhost:5000/blogs')
            .then(res => {
                return res.json()
            })
            .then(data => {
                setBlogs(data)
            })
    }, []) 

    //add a blog

    //delete a blog
    const deleteBlog = async (id) => {
        const res = await fetch(`http://localhost:5000/blogs/${id}`, {
            method: 'DELETE'
        })

        //gotta check if the api call was successful
        const newBlog = blogs.filter(
            blog => blog.id !== id
        )
        setBlogs(newBlog)
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            { blogs.length > 0 ? (<BlogList blogs={blogs} deleteBlog={deleteBlog} />) : ('No blogs yet.')}
        </div>
    );
}

export default Home;
