import BlogList from './BlogList';
import Loading from './Loading';
import Error from './Error';
import useFetch from './useFetch';

const Home = ({ title }) => {
    const { data, isLoading, error } = useFetch('http://192.168.1.98:5000/blogs')

    //add a blog



    return (
        <div className="home">
            <h1>{title}</h1>
            { error && <Error/> }
            { isLoading && <Loading /> }
            { data && <BlogList blogs={data} />}
        </div>
    );
}

export default Home;
