import React, { useState } from 'react'

const Home = ({ title }) => {
    const [name, setName] = useState('Kayode')
    const handleClick = () => {
        setName("Ogunmakinwa")
    }

    return (
        <div className="home">
            <h1>{title}</h1>
            <p>{name}</p>
            <button onToggle={handleClick}>Click Me</button>
        </div>
    );
}

export default Home;
