import useMediaQuery from '@material-ui/core/useMediaQuery';
import NavBarMenu from './NavBarMenu'
import { useState } from 'react';

const Navbar = () => {
    const matches = useMediaQuery("(max-width: 480px)");
    const [isOpen, setIsOpen] = useState(false)

    const openMenu = () => {
        setIsOpen(true)
    }

    const closeMenu = () => {
        setIsOpen(false)
    }

    return (
        <>
            <nav className="navbar">
                <a href="/"><h1>Mini Blog</h1></a>

                {matches === true
                    ? <NavBarMenu isOpen={isOpen} setIsOpen={setIsOpen} openMenu={openMenu} closeMenu={closeMenu} />

                    : <div className="links">
                        <a href="/">Home</a>
                        <a href="/create">New Blog</a>
                    </div>
                }
            </nav>

            { 
                isOpen && <div className="nav-mobile">
                    <a href="/">Home</a>
                    <a className="last-a" href="/create">New Blog</a>
                </div>
             }
        </>
    );
}

export default Navbar;
