import useMediaQuery from '@material-ui/core/useMediaQuery';
import NavBarMenu from './NavBarMenu'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const matches = useMediaQuery("(max-width: 480px)");
    const [isOpen, setIsOpen] = useState(false)
    const [clicked, setClicked] = useState(false);

    const openMenu = () => {
        setIsOpen(true)
        setClicked(true)
    }
    const closeMenu = () => {
        setIsOpen(false)
        setClicked(true)
    }
    const handleScroll = () => {
        isOpen ? closeMenu() : setIsOpen(false)
        // window.removeEventListener('scroll', handleScroll, { capture: false });
    }
    window.addEventListener('scroll', handleScroll, { capture: true });


    return (
		<div className="main-nav">
			<nav className="navbar">
				<Link to="/">
					<h1>Mini Blog</h1>
				</Link>

				{matches === true ? (
					<NavBarMenu clicked={clicked} isOpen={isOpen} openMenu={openMenu} closeMenu={closeMenu} />
				) : (
					<div className="links">
						<Link to="/">Home</Link>
						<Link to="/create">New Blog</Link>
					</div>
				)}
			</nav>
			{isOpen && (
				<div className="nav-mobile nav-fadeIn">
					<Link to="/" onClick={closeMenu}>
						Home
					</Link>
					<Link className="last-a" to="/create" onClick={closeMenu}>
						New Blog
					</Link>
				</div>
			) }

		</div>

	);
}

export default Navbar;
